import React from 'react';
import './Candidates.css';

const Candidates = () => {
  const candidates = [
    {
      id: 1,
      name: 'Juan Pérez',
      position: 'Candidato a Presidente',
      image: 'https://randomuser.me/api/portraits/men/32.jpg',
      bio: 'Economista con más de 20 años de experiencia en el sector público y privado. Comprometido con el desarrollo sostenible de Bolivia.'
    },
    {
      id: 2,
      name: 'María González',
      position: 'Candidata a Vicepresidenta',
      image: 'https://randomuser.me/api/portraits/women/44.jpg',
      bio: 'Abogada especializada en derechos humanos y políticas públicas. Luchadora incansable por la igualdad y la justicia social.'
    },
    {
      id: 3,
      name: 'Carlos Rojas',
      position: 'Candidato a Senador',
      image: 'https://randomuser.me/api/portraits/men/22.jpg',
      bio: 'Ingeniero agrónomo con amplia experiencia en desarrollo rural y políticas agrícolas. Defensor de los pequeños productores.'
    },
    {
      id: 4,
      name: 'Ana Vargas',
      position: 'Candidata a Diputada',
      image: 'https://randomuser.me/api/portraits/women/68.jpg',
      bio: 'Educadora y activista social con una larga trayectoria en la defensa de los derechos de la niñez y la juventud.'
    },
    {
      id: 5,
      name: 'Roberto Méndez',
      position: 'Candidato a Alcalde',
      image: 'https://randomuser.me/api/portraits/men/45.jpg',
      bio: 'Arquitecto urbanista comprometido con el desarrollo urbano sostenible y la planificación de ciudades más humanas.'
    },
    {
      id: 6,
      name: 'Laura Castro',
      position: 'Candidata a Concejal',
      image: 'https://randomuser.me/api/portraits/women/33.jpg',
      bio: 'Comunicadora social especializada en políticas públicas de comunicación. Promotora de la transparencia y el acceso a la información.'
    }
  ];

  return (
    <section className="candidates-section">
      <div className="container">
        <h2 className="section-title">Nuestros Candidatos</h2>
        <p className="section-subtitle">Conoce al equipo que trabajará por el cambio que Bolivia necesita</p>

        <div className="candidates-grid">
          {candidates.map((candidate) => (
            <div key={candidate.id} className="candidate-card">
              <div className="candidate-image">
                <img src={candidate.image} alt={candidate.name} />
                <div className="candidate-overlay">
                  <div className="social-links">
                    <a href="#" aria-label="Facebook"><i className="fab fa-facebook-f"></i></a>
                    <a href="#" aria-label="Twitter"><i className="fab fa-twitter"></i></a>
                    <a href="#" aria-label="Instagram"><i className="fab fa-instagram"></i></a>
                  </div>
                </div>
              </div>
              <div className="candidate-info">
                <h3>{candidate.name}</h3>
                <span className="position">{candidate.position}</span>
                <p>{candidate.bio}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Candidates;
