import React, { useState } from 'react';
import './Join.css';

const Join = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phone: '',
    interestArea: ''
  });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));

    // Clear error when user types
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.fullName.trim()) {
      newErrors.fullName = 'El nombre completo es requerido';
    }

    if (!formData.email) {
      newErrors.email = 'El correo electrónico es requerido';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'El correo electrónico no es válido';
    }

    if (!formData.phone) {
      newErrors.phone = 'El teléfono es requerido';
    } else if (!/^[0-9+\-\s]+$/.test(formData.phone)) {
      newErrors.phone = 'Ingrese un número de teléfono válido';
    }

    if (!formData.interestArea.trim()) {
      newErrors.interestArea = 'Por favor indique su área de interés';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Aquí iría la lógica para enviar los datos a un servidor
      console.log('Formulario enviado:', formData);
      setIsSubmitted(true);
      // Reset form
      setFormData({
        fullName: '',
        email: '',
        phone: '',
        interestArea: ''
      });
    }
  };

  if (isSubmitted) {
    return (
      <section className="join-section">
        <div className="container">
          <div className="thank-you-message">
            <i className="fas fa-check-circle"></i>
            <h2>¡Gracias por unirte!</h2>
            <p>Hemos recibido tu solicitud. Pronto nos pondremos en contacto contigo para darte más información sobre cómo puedes ser parte del cambio.</p>
            <button 
              className="btn" 
              onClick={() => setIsSubmitted(false)}
            >
              Volver al formulario
            </button>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="join-section">
      <div className="container">
        <div className="join-content">
          <div className="join-info">
            <h2>Únete a Nuestro Equipo</h2>
            <p>El Partido Demócrata Cristiano está buscando personas comprometidas con el cambio y el progreso de Bolivia. Únete a nosotros y sé parte de este movimiento que busca construir un futuro mejor para todos.</p>

            <div className="benefits">
              <h3>Beneficios de unirte:</h3>
              <ul>
                <li><i className="fas fa-check"></i> Forma parte de un equipo comprometido con el cambio</li>
                <li><i className="fas fa-check"></i> Accede a capacitaciones y talleres exclusivos</li>
                <li><i className="fas fa-check"></i> Participa en la toma de decisiones del partido</li>
                <li><i className="fas fa-check"></i> Conviértete en agente de cambio en tu comunidad</li>
              </ul>
            </div>
          </div>

          <div className="join-form-container">
            <form className="join-form" onSubmit={handleSubmit}>
              <h3>Completa el formulario</h3>

              <div className="form-group">
                <label htmlFor="fullName">Nombre completo</label>
                <input
                  type="text"
                  id="fullName"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  className={errors.fullName ? 'error' : ''}
                  placeholder="Ingresa tu nombre completo"
                />
                {errors.fullName && <span className="error-message">{errors.fullName}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="email">Correo electrónico</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className={errors.email ? 'error' : ''}
                  placeholder="tucorreo@ejemplo.com"
                />
                {errors.email && <span className="error-message">{errors.email}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="phone">Teléfono</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  className={errors.phone ? 'error' : ''}
                  placeholder="Ej: +591 700 00000"
                />
                {errors.phone && <span className="error-message">{errors.phone}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="interestArea">Área de interés</label>
                <textarea
                  id="interestArea"
                  name="interestArea"
                  value={formData.interestArea}
                  onChange={handleChange}
                  className={errors.interestArea ? 'error' : ''}
                  placeholder="¿En qué área te gustaría participar o qué te motiva a unirte?"
                  rows="4"
                ></textarea>
                {errors.interestArea && <span className="error-message">{errors.interestArea}</span>}
              </div>

              <button type="submit" className="btn btn-block">Enviar solicitud</button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Join;
